mapper.select:
 SELECT hp_code, hp_name, dept_name, dept_code FROM mks_online
	WHERE mks_id=#{mks_id}
	AND mks_pw=#{mks_pw}

SELECT b.board_no, b.board_content, b.board_date, b.board_title, b.board_file, o.dept_name,b.mks_id
 FROM mks_board b, mks_online o
 WHERE b.mks_id = o.mks_id
  AND o.hp_code=#{hp_code}

 	AND b.board_no=#{board_no}

 ORDER BY b.board_no desc

SELECT b.board_no , b.board_title, o.hp_code FROM mks_board b, mks_online o WHERE b.mks_id = o.mks_id AND o.hp_code=#{hp_code}
AND b.board_title LIKE '%' || #{board_title} || '%' OR o.dept_name LIKE '%' || #{dept_name}|| '%'
ORDER BY b.board_no desc

SELECT MIN(mem.mem_name) mem_name , mem.mem_memcode mem_memcode, MIN(mem.mem_socialnum) mem_socialnum,MIN(mem.mem_phone) mem_phone FROM MKS_MEMBER mem , MKS_HISTORY his WHERE mem.mem_memcode = his.mem_memcode
AND his.hp_code=#{hp_code}
GROUP BY mem.mem_memcode ORDER BY mem.mem_memcode

SELECT MIN(mem.mem_name) mem_name , mem.mem_memcode mem_memcode, MIN(mem.mem_socialnum) mem_socialnum,MIN(mem.mem_phone) mem_phone FROM MKS_MEMBER mem , MKS_HISTORY his WHERE mem.mem_memcode = his.mem_memcode
AND his.hp_code=#{hp_code}
AND mem.mem_name=#{mem_name}
AND mem.mem_socialnum LIKE '%'||#{mem_socialnum}||'%'
GROUP BY mem.mem_memcode ORDER BY mem.mem_memcode

SELECT mem.mem_name mem_name , mem.mem_memcode mem_memcode, mem.mem_socialnum mem_socialnum,mem.mem_phone mem_phone , his.his_date his_date , HIS.HIS_CONTENT his_content , mem.mem_address mem_address , HIS.DOC_NAME doc_name ,HIS.DEPT_NAME dept_name FROM MKS_MEMBER mem , MKS_HISTORY his WHERE mem.mem_memcode = his.mem_memcode
AND his.hp_code=#{hp_code}
AND mem.mem_memcode=#{mem_code}
ORDER BY mem.mem_memcode

mapper.update:
UPDATE mks_doctor
       set

       doc_code = #{doc_code}


      ,hp_code = #{hp_code}


      ,dept_name = #{dept_name}


      ,doc_name = #{doc_name}


      ,doc_position = #{doc_position}


      ,doc_education = #{doc_education}


      ,doc_phone = #{doc_phone}


      ,doc_offday = #{doc_offday}


      ,doc_state = #{doc_state}



 		and doc_code = #{doc_code}

mapper.delete:
DELETE FROM mks_history
WHERE hp_code = #{hp_code}
AND mem_memcode=#{mem_code}

mapper.update:
UPDATE MKS_MEMBER 
SET MEM_NAME=#{mem_name} ,  mem_phone=#{mem_phone} ,mem_address=#{mem_address} 
WHERE mem_memcode=#{mem_code}
