mapper.select:
SELECT MIN(mem.mem_name) mem_name , mem.mem_memcode mem_memcode, MIN(mem.mem_socialnum) mem_socialnum,MIN(mem.mem_phone) mem_phone FROM MKS_MEMBER mem , MKS_HISTORY his WHERE mem.mem_memcode = his.mem_memcode
AND his.hp_code=#{hp_code}
GROUP BY mem.mem_memcode ORDER BY mem.mem_memcode

SELECT MIN(mem.mem_name) mem_name , mem.mem_memcode mem_memcode, MIN(mem.mem_socialnum) mem_socialnum,MIN(mem.mem_phone) mem_phone FROM MKS_MEMBER mem , MKS_HISTORY his WHERE mem.mem_memcode = his.mem_memcode
AND his.hp_code=#{hp_code}
AND mem.mem_name=#{mem_name}
AND mem.mem_socialnum LIKE '%'||#{mem_socialnum}||'%'
GROUP BY mem.mem_memcode ORDER BY mem.mem_memcode

SELECT mem.mem_name mem_name , mem.mem_memcode mem_memcode, mem.mem_socialnum mem_socialnum,mem.mem_phone mem_phone , his.his_date his_date , HIS.HIS_CONTENT his_content , mem.mem_address mem_address , HIS.DOC_NAME doc_name ,HIS.DEPT_NAME dept_name FROM MKS_MEMBER mem , MKS_HISTORY his WHERE mem.mem_memcode = his.mem_memcode
AND his.hp_code=#{hp_code}
AND mem.mem_memcode=#{mem_code}
ORDER BY mem.mem_memcode

mapper.delete:
DELETE FROM mks_history
WHERE hp_code = #{hp_code}
AND mem_memcode=#{mem_code}
