mapper.select:
SELECT r, hp_code, hp_name , hp_lat, hp_lng, hp_detail, hp_address, hp_open, hp_close, hp_phone, dept_name FROM ( SELECT ROWNUM r, hp_code, hp_name, hp_lat, hp_lng, hp_detail, hp_address, hp_open, hp_close, hp_phone, dept_name FROM ( SELECT h.hp_code , MIN(h.hp_name) hp_name , MIN(h.hp_lat) hp_lat ,MIN(h.hp_lng) hp_lng , MIN(h.hp_detail) hp_detail, MIN(h.hp_address) hp_address , MIN(h.hp_open) hp_open , MIN(h.hp_close) hp_close , MIN(h.hp_phone) hp_phone ,WM_CONCAT(d.dept_name) dept_name FROM mks_hospital h , mks_dept d WHERE h.hp_code= d.hp_code GROUP BY h.hp_code ) WHERE 1 = 1
AND (SELECT SIGN((TO_DATE(hp_open, 'HH24:MI') - TO_DATE('9:00','HH24:MI'))) FROM dual) = -1
AND (SELECT SIGN((TO_DATE(hp_close, 'HH24:MI') - TO_DATE('18:00','HH24:MI'))) FROM dual) = 1
AND hp_name LIKE '%'||#{hp_name}||'%'
)
WHERE r BETWEEN #{first} AND #{end}


