mapper.select:
{call proc_mem_login (
   #{u_id, mode=IN, jdbcType=VARCHAR}
   ,#{u_pw, mode=IN, jdbcType=VARCHAR}                  
   ,#{msg, mode=OUT, jdbcType=VARCHAR}                  
   ,#{msg_id, mode=OUT, jdbcType=VARCHAR}                  
