SELECT r, board_no, board_title, board_date, mks_id FROM ( SELECT ROWNUM r, board_no, board_title, board_date, mks_id FROM ( SELECT board_no, board_title, board_date, mks_id FROM mks_board WHERE board_type = 0
AND board_title LIKE '%'||#{board_title}||'%'
ORDER BY board_date desc )
WHERE ROWNUM <= 2
)
WHERE r BETWEEN #{fnum} AND #{enum}


SELECT 
    dept_prewait, (dept_lastwait - dept_prewait)*2
  FROM mks_dept
  WHERE hp_code = #{hp_code}
  AND dept_name = #{dept_name}


SELECT
    hp_code, hp_name, doc_code, doc_name, dept_code, dept_name, sch_code, sch_time, to_char(to_date(sch_date), 'YYYY-MM-DD') sch_date 
  FROM mks_schedule   
 WHERE hp_code = #{hp_code}
   AND dept_code = #{dept_code} 
   AND doc_code = #{doc_code}   
   AND sch_date = #{sch_date}
   AND sch_code NOT IN (
                        SELECT sch_code
                          FROM mks_reserve
                         WHERE hp_code = #{hp_code}
                           AND dept_code = #{dept_code} 
                           AND doc_code = #{doc_code}   
                           AND sch_date = #{sch_date}
                          )
 ORDER BY sch_date, sch_time asc

SELECT rnum, mem_memcode, hp_name, dept_name, doc_name, TO_CHAR(TO_DATE(sch_date),'YYYY-MM-DD') sch_date, res_qrcode, res_time, sch_code FROM ( SELECT rownum rnum, mem_memcode, hp_name, dept_name, doc_name, res_qrcode, sch_date, res_time, sch_code FROM mks_reserve WHERE mem_memcode = (SELECT mem_memcode FROM mks_online WHERE mks_id = #{mks_id})
AND doc_name LIKE '%'||#{doc_name}||'%'
AND (SELECT SIGN(TO_DATE(sch_date,'YYYY-MM-DD') - TO_DATE(TO_CHAR(SYSDATE,'YYYY-MM-DD'),'YYYY-MM-DD')) FROM dual) = 1
AND (SELECT SIGN(TO_DATE(sch_date,'YYYY-MM-DD') - TO_DATE(TO_CHAR(SYSDATE,'YYYY-MM-DD'),'YYYY-MM-DD')) FROM dual) = -1
) WHERE 1 = 1
AND rnum BETWEEN #{fnum} and #{enum}


SELECT NVL(
            (SELECT
                mks_pw
              FROM mks_online 
             WHERE mks_id = #{mks_id}
               AND mks_pw = #{mks_pw}
            ), ''
          ) mks_pw
  FROM dual

mapper.update:

UPDATE mks_member
   SET
        mem_address = #{mem_address}
       ,mem_phone = #{mem_phone}
 WHERE mem_memcode = (SELECT mem_memcode FROM mks_online WHERE mks_id = #{mks_id})


UPDATE mks_online
   SET
        mks_pw = #{mks_pw}
       ,mks_email = #{mks_email}
 WHERE mem_memcode = (SELECT mem_memcode FROM mks_online WHERE mks_id = #{mks_id})

