SELECT rnum, eva_code, sch_code, mks_id, eva_title, eva_date, hp_name, step, pos, group_no, eva_content, hit FROM ( SELECT rownum rnum, eva_code, sch_code, mks_id, eva_title, eva_date, hp_name, step, pos, group_no, eva_content, hit FROM ( SELECT a.eva_code, a.sch_code, b.mks_id, a.eva_title, a.eva_date, a.hp_name, a.step, a.pos, a.group_no, a.eva_content, a.hit FROM mks_eva a, mks_online b WHERE a.mem_memcode = b.mem_memcode AND pos = 1
AND eva_code = #{eva_code}
AND eva_title LIKE '%'||#{eva_title}||'%'
AND b.mks_id LIKE '%'||#{mks_id}||'%'
AND a.hp_name LIKE '%'||#{hp_name}||'%'
ORDER BY hit desc, eva_code desc
ORDER BY eva_code desc
) )
WHERE rnum BETWEEN #{fnum} AND #{enum}

mapper.update:
UPDATE mks_eva 
   SET hit = (SELECT nvl(hit,0) FROM mks_eva WHERE eva_code=#{eva_code})+1
 WHERE eva_code = #{eva_code}

		UPDATE mks_eva 
   	   	   SET 
               eva_content=#{eva_content}
              ,eva_title=#{eva_title}
  		 WHERE eva_code = #{eva_code}

mapper.select:
SELECT rnum, board_no, board_content, board_date, board_title, hit, mks_id, good, bad FROM ( SELECT rownum rnum, board_no, board_content, board_date, board_title, hit, mks_id, good, bad FROM mks_board WHERE board_type=1
AND board_title LIKE '%'||#{i_title}||'%'
AND board_no = #{board_no}
ORDER BY hit desc, board_no desc
ORDER BY board_no desc
)
WHERE rnum BETWEEN #{fnum} AND #{enum}

mapper.update:
UPDATE mks_board
SET
board_content=#{board_content},
board_title = #{board_title}
WHERE board_no = #{board_no}
